	"type": "<documentation|mock|monitor|testsuite|contracttest|integrationtest>",
	"schemaId": "<Schema id>",
	"configurations": {
		"converter": {
			"folderStrategy": "Paths",
			"collapseFolders": true,
			...Other options
		},
		"collection": {"name": "Name of collection"},
		"<mock|monitor>": {"name": "Name of entity", ...other mock and monitor options}
	}
}

// To create mock or monitor from collection
{
	"type": "<mock|monitor>",
	"collectionId": "<collection id>",
	"configurations": {
		"<mock|monitor>": {"name": "Name of entity", ...other mock and monitor options}
	}
}

// To link a schema
{
	"type": "schema",
    "model": "schema",
	"modelId": "<schema id>"
}

